import axios from 'axios';
export default {
  data() {
    return {
      tableData: []
    };
  },
  methods: {
    tableRowClassName({
      row
    }) {
      // row 是当前行的数据对象
      if (row.state === '已完成') {
        return 'success-row';
      }
      return ''; // 默认情况下返回空字符串，不添加额外的 class
    }
  },

  // 在 mounted 钩子中
  mounted() {
    const examId = this.$route.params.examId;
    console.log(examId);
    axios({
      method: 'get',
      url: 'http://localhost:4399/exam/getExamById',
      params: {
        examId: examId // 在 params 对象中传递参数
      }
    }).then(res => {
      this.tableData = res.data.data;
    });
  }
};
