import axios from 'axios';
export default {
  data() {
    return {
      tableData: []
    };
  },
  methods: {},
  mounted() {
    const examId = this.$route.params.id;
    axios({
      method: 'get',
      url: 'http://localhost:4399/exam/getExamById',
      params: examId
    }).then(res => {
      this.tableData = res.data.data;
    });
  }
};
